import styled from 'styled-components'

export const MainWrapper = styled.div`
    border: 1px solid white;
    border-radius: 0.5em;
    grid-row: 1;
    grid-column: 1/3;
`

export const Logo = styled.img`
    width: 50%;
    height: 50%;
    display: inline;
    margin-left: 0;
    cursor: pointer;
`

export const Link = styled.a`
    text-decoration: none;
    color: inherit;
`
