import { useState } from "react"

const Messages = () => {

    const [messages, setMessage] = useState([
        {
            Author: 'Александр Иванов',
            Avatar: 'https://www.blast.hk/attachments/64804/',
            Message: 'Привет, как у тебя дела?',
            id: id
        },
        {
            Author: 'Татьяна Притыкина',
            Avatar: 'https://i.pinimg.com/550x/a4/1d/da/a41ddae55fef329b4f74295ba758a424.jpg',
            Message: 'Как там Саня?',
            id: id
        }
    ])

    const createMessage = (authorName: string, text: string, id: number) => {
        return {
            Author: authorName,
            Avatar: 'https://www.blast.hk/attachments/64804/',
            Message: text,
            id: id
        }
    }

    return (
        <div>
            <h1>Messages</h1>
        </div>
    )
}

export default Messages
