import styled from "@emotion/styled"

export const FriendsWrapper = styled.div`
    grid-row: 1;
    grid-column: 1;
    border-radius: .5em;
    box-shadow: 2px 2px 5px azure;
    width: 30em;
    h1 {
        font-size: 2em;
        text-align: center;
        margin-top: 1em;
    }
`
