import styled from "styled-components";

export const SidebarWrapper = styled.div `
    grid-row: 2;
    grid-column: 1;
    text-align: center;
    margin-left: 18em;
    height: 20em;
`

export const NavMenu = styled.nav`
    display: flex;
    align-items: flex-start;
    flex-direction: column;
    align-items: center;
`

export const NavLink = styled.a`
    text-decoration: none;
    color: inherit;

`
