import styled from "@emotion/styled";

export const MessageList = styled.div`
    height: fit-content;
`

export const MessageItem = styled.div`
    display: grid;
    width: 70%;
    height: 4em;
    box-shadow: 0 0 10px #181818;
    margin-bottom: .75em;
    padding: .75em;
    grid-template-rows: 1.5em 1fr;
    grid-template-columns: 2em 1fr;
    cursor: pointer;
    border-radius: .5em;
    h5 {
        grid-row: 1;
        grid-column: 2;
        margin-left: 1em;
        font-size: 1em;
    }
    p{
        grid-row: 2;
        grid-column: 2;
        margin-left: 1.25em;
    }
    :hover {
        background-color: #444;
        transition: 0.3s;
    }
`

export const MessageAuthorImage = styled.img`
    width: 3em;
    border-radius: 50%;
    grid-row: 1/3;
    grid-column: 1;
`
