import { useEffect, useState } from "react";
import AddPostForm from "./AddPostForm";
import { DeleteButton, Item, ItemImage, PostWrapper } from "./Posts.styles";

const Posts = () => {

    const [id, setId] = useState<number>(0)

    const createPost = (label: string, id: number) => {
        return {
            Author: 'Владислав Самсонов',
            Message: label,
            Avatar: 'https://d2zia2w5autnlg.cloudfront.net/46235/5ec33ee77313a-large',
            id: id
        }
    }

    const [posts, setPost] = useState([
        createPost('Lorem ipsum dolor sit, amet consectetur adipisicing elit. Impedit quae ut suscipit harum expedita optio, incidunt aliquid saepe officiis sapiente repellendus, numquam libero, perferendis excepturi? Tempore dolorem maiores voluptatibus illum ducimus distinctio odit at, molestiae commodi soluta inventore minima, quidem dolorum aliquam blanditiis incidunt deserunt voluptate rem ipsam fugit eaque fuga debitis expedita. Sint, voluptas porro odit dicta fugiat sunt ex veritatis nam impedit non architecto velit exercitationem delectus rerum asperiores. Tempore id officia officiis, expedita soluta debitis nam ipsum aliquid molestias reiciendis veritatis provident ad. Nisi exercitationem vel fuga laborum minus, minima nobis libero veniam blanditiis rerum fugit perspiciatis.', 0)
    ])

    const addPost = (text: string) => {
        if (text !== ''){
            const newPost = createPost(text, id + 1)
            const newArray = [...posts, newPost]
            setId(id + 1)
            setPost(newArray)
        }
    }

    const deletePost = (id: number) => {
        setPost(posts.filter((post) => post.id !== id))
    }

    return (
        <PostWrapper >
            <AddPostForm addPost={addPost} />
            {posts.map((userPost) => (
                <Item key={userPost.id}>
                    <ItemImage src={userPost.Avatar} alt='Фотография профиля' />
                    <h5>{userPost.Author}</h5>
                    <p>{userPost.Message}</p>
                    <DeleteButton
                        type='button'
                        className="btn btn-outline-danger"
                        title="Удалить пост"
                        onClick={() => deletePost(userPost.id)} />
                </Item>
            ))}
        </PostWrapper>
    )
}

export default Posts
