import styled from "@emotion/styled";

export const ProfileInformation = styled.div`
    display: inline-flex;
    width: 100%;
    justify-content: space-between;
    padding: 0 3em 0 3em;
`

export const PostWrapper = styled.div`
    display: inline-flex;
    flex-direction: column;
    width: 100%;
    margin-top: 2em;
    align-items: center;
`
