import { useState } from "react"

const Messages = () => {

    const [messages, setMessage] = useState([createMessage('Александр Иванов', "Привет, как твои дела?", 0)])

    const createMessage = (authorName: string, text: string, id: number) => {
        return {
            Author: authorName,
            Avatar: 'https://www.blast.hk/attachments/64804/',
            Message: text,
            id: id
        }
    }

    return (
        <div>
            <h1>Messages</h1>
        </div>
    )
}

export default Messages
