import styled from "@emotion/styled";

export const FriendListWrapper = styled.div`
    width: 100%;
    height: 9em;
    margin-top: 1em;
    box-shadow: 0 0 10px #181818;
    padding: .85em;
    display: flex;
    img {
        width: 6em;
        border-radius: 2em;
        object-fit: cover;
    }
    h1 {
        margin: .1em 0 0 1em;
        font-size: 2em;
    }
    :first-child {
        margin: 0;
    }
`
