import { useState } from "react"
import { MessageAuthorImage, MessageItem, MessageList } from "./Messages.styles"

const Messages = () => {

    
    const createMessage = (authorName: string, text: string, id: number) => {
        return {
            Author: authorName,
            Avatar: 'https://www.blast.hk/attachments/64804/',
            Message: text,
            id: id
        }
    }

    const [messages, setMessage] = useState([createMessage('Александр Иванов', "Привет, как твои дела?", 0)])

    return (
        <MessageList >
            {messages.map((message) => {
                <MessageItem >
                    <MessageAuthorImage src={message.Avatar}/>
                    <h5>{message.Author}</h5>
                    <p>{message.Message}</p>
                </MessageItem>
            })}
        </MessageList>
    )
}

export default Messages
